import React, { Component } from "react";
import gql from "graphql-tag";
import { compose, graphql } from "react-apollo";

const GetFkNodes = gql`
  query {
    getFkNodes {
      nodeId
      nodeName
      pathNameString
      pathString
      pathLength
    }
  }
`;

const GetAmNodes = gql`
  query {
    getAmNodes {
      nodeId
      nodeName
      pathNameString
      pathString
      pathLength
    }
  }
`;

const GetHdNodes = gql`
  query {
    getHdNodes {
      nodeId
      nodeName
      pathNameString
      pathString
      pathLength
    }
  }
`;

const GetHdNodesAsObj = gql`
  query {
    getHdNodesAsObj {
        
    }
  }
`;

class ManageNodes extends Component {
  state = {
    showFlipkartNodes: true,
    showAmazonNodes: false,
    fkNodes: {},
    amNodes: {},
    hdNodes: {}
  };

  switchRetailerView = () => {
    this.setState(
      {
        showFlipkartNodes: !this.state.showFlipkartNodes,
        showAmazonNodes: !this.state.showAmazonNodes
      },
      function() {
        if (this.state.showFlipkartNodes) {
          document.getElementById("flipkart-outer-div").style.display = "block";
        } else {
          document.getElementById("flipkart-outer-div").style.display = "none";
        }
        if (this.state.showAmazonNodes) {
          document.getElementById("amazon-outer-div").style.display = "block";
        } else {
          document.getElementById("amazon-outer-div").style.display = "none";
        }
      }
    );
  };

  rearrangeArray = nodeArray => {
    var temp = {};

    for (var x = 0; x < nodeArray.length; x++) {
      var node = nodeArray[x];
      var tempKey = "";
      if (node.pathLength > 0) {
        tempKey = node.pathNameString + ">" + node.nodeName;
      } else {
        tempKey = node.nodeName;
      }
      temp[tempKey] = {
        nodeId: node.nodeId,
        nodeName: node.nodeName,
        pathNameString: node.pathNameString,
        pathString: node.pathString,
        pathLength: node.pathLength,
        tempPath: tempKey
      };
    }
    return temp;
  };

  setupFkNodes = async () => {
    console.log("i was here");
    // var divId = "flipkart-outer-div";
    var nodeArray = this.props.getFkNodes.getFkNodes;
    var temp = await this.rearrangeArray(nodeArray);
    this.setState(
      {
        fkNodes: temp
      },
      function() {
        console.log(this.state.fkNodes);
      }
    );
  };
  setupAmNodes = async () => {
    console.log("i was here 2");
    // var divId = "amazon-outer-div";
    var nodeArray = this.props.getAmNodes.getAmNodes;
    var temp = await this.rearrangeArray(nodeArray);
    this.setState(
      {
        amNodes: temp
      },
      function() {
        console.log(this.state.amNodes);
      }
    );
  };
  setupHdNodes = async () => {
    console.log("i was here 3");
    // var divId = "amazon-outer-div";
    var nodeArray = this.props.getHdNodes.getHdNodes;
    var temp = await this.rearrangeArray(nodeArray);
    this.setState(
      {
        hdNodes: temp
      },
      function() {
        console.log(this.state.hdNodes);
      }
    );
  };
  setupHdNodesAsObj = () => {
    var nodesObj = this.props.getHdNodesAsObj.getHdNodesAsObj;
  };

  refreshTrees = () => {
    this.setupHdNodes();
    this.setupFkNodes();
    this.setupAmNodes();
  };

  render() {
    console.log(this.props);

    const loadingFk = this.props.getFkNodes.loading;
    const getFkNodes = this.props.getFkNodes.getFkNodes;
    const loadingAm = this.props.getAmNodes.loading;
    const getAmNodes = this.props.getAmNodes.getAmNodes;
    const loadingHd = this.props.getHdNodes.loading;
    const getHdNodes = this.props.getHdNodes.getHdNodes;

    if (loadingFk) {
      return false;
    }

    if (loadingAm) {
      return false;
    }

    if (loadingHd) {
      return false;
    }

    return (
      <div className="custom-outer-div">
        <div className="custom-box-container">
          <div className="inner-container">
            {/* <div className="header">Manage Huddlie Categories</div> */}
            <div className="header-no-border">
              <label className="label-with-underline">Manage Categories</label>
            </div>

            <button className="login-button" onClick={this.refreshTrees}>
              Refresh
            </button>
            <div className="mc-row">
              <div id="retailer-outer-div" className="mc-row-50">
                <div className="header-no-border">Retailer Categories</div>
                <div className="custom-box-controller">
                  <div
                    className={
                      this.state.showFlipkartNodes
                        ? "controller selected-controller"
                        : "controller"
                    }
                    onClick={this.switchRetailerView}
                  >
                    Flipkart
                  </div>
                  <div
                    className={
                      this.state.showAmazonNodes
                        ? "controller selected-controller"
                        : "controller"
                    }
                    onClick={this.switchRetailerView}
                  >
                    Amazon
                  </div>
                </div>
                <div id="flipkart-outer-div">Flipkart Nodes</div>
                <div id="amazon-outer-div">Amazon Nodes</div>
              </div>
              <div id="huddlie-outer-div" className="mc-row-50">
                <div className="header-no-border">Huddlie Categories</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default compose(
  graphql(GetFkNodes, { name: "getFkNodes" }),
  graphql(GetAmNodes, { name: "getAmNodes" }),
  graphql(GetHdNodes, { name: "getHdNodes" }),
  graphql(GetHdNodesAsObj, { name: "getHdNodesAsObj" })
)(ManageNodes);
