import React, { Component } from "react";
import gql from "graphql-tag";
import { compose, graphql } from "react-apollo";
import values from "lodash/values";
// import TreeNode from "./treeNode";

const GetNodesAsObj = gql`
  mutation getNodesAsObj($retailer: String!) {
    getNodesAsObj(retailer: $retailer) {
      nodes
    }
  }
`;

class ManageNodes extends Component {
  state = {
    showFlipkartNodes: true,
    showAmazonNodes: false,
    fkNodes: {},
    amNodes: {},
    hdNodes: {},
    nodes: {}
  };

  getRootNodes = () => {
    const { nodes } = this.state;
    return values(nodes).filter(node => node.pathLength === 0);
  };

  getChildNodes = node => {
    const { nodes } = this.state;
    console.log(
      values(nodes).filter(node2 => node2.pathNameString === node.tempPath)
    );
  };

  onToggle = node => {
    const { nodes } = this.state;
    nodes[node.tempPath].isOpen = !node.isOpen;
    this.setState({ nodes });
  };

  switchRetailerView = () => {
    this.setState(
      {
        showFlipkartNodes: !this.state.showFlipkartNodes,
        showAmazonNodes: !this.state.showAmazonNodes
      },
      function() {
        if (this.state.showFlipkartNodes) {
          document.getElementById("flipkart-outer-div").style.display = "block";
        } else {
          document.getElementById("flipkart-outer-div").style.display = "none";
        }
        if (this.state.showAmazonNodes) {
          document.getElementById("amazon-outer-div").style.display = "block";
        } else {
          document.getElementById("amazon-outer-div").style.display = "none";
        }
      }
    );
  };

  setupNodesAsObj = async () => {
    var nodesObj = await this.props.GetNodesAsObj({
      variables: {
        retailer: "hd"
      }
    });
    var nodes = {};
    // console.log(nodesObj);
    if (
      nodesObj &&
      nodesObj.data &&
      nodesObj.data.getNodesAsObj &&
      nodesObj.data.getNodesAsObj.nodes &&
      nodesObj.data.getNodesAsObj.nodes !== ""
    ) {
      nodes = JSON.parse(nodesObj.data.getNodesAsObj.nodes);
    }
    this.setState({ nodes: nodes }, function() {
      console.log(nodes);
    });
  };

  refreshTrees = () => {
    this.setupNodesAsObj();
  };

  render() {
    console.log(this.props);

    return (
      <div className="custom-outer-div">
        <div className="custom-box-container">
          <div className="inner-container">
            {/* <div className="header">Manage Huddlie Categories</div> */}
            <div className="header-no-border">
              <label className="label-with-underline">Manage Categories</label>
            </div>

            <button className="login-button" onClick={this.refreshTrees}>
              Refresh
            </button>
            <div className="mc-row">
              <div id="retailer-outer-div" className="mc-row-50">
                <div className="header-no-border">Retailer Categories</div>
                <div className="custom-box-controller">
                  <div
                    className={
                      this.state.showFlipkartNodes
                        ? "controller selected-controller"
                        : "controller"
                    }
                    onClick={this.switchRetailerView}
                  >
                    Flipkart
                  </div>
                  <div
                    className={
                      this.state.showAmazonNodes
                        ? "controller selected-controller"
                        : "controller"
                    }
                    onClick={this.switchRetailerView}
                  >
                    Amazon
                  </div>
                </div>
                <div id="flipkart-outer-div">Flipkart Nodes</div>
                <div id="amazon-outer-div">Amazon Nodes</div>
              </div>
              <div id="huddlie-outer-div" className="mc-row-50">
                <div className="header-no-border">Huddlie Categories</div>
                {this.state.nodes !== {} &&
                  this.getRootNodes().map(node => (
                    <div key={node.nodeId}>
                      <label>{node.nodeName}</label>
                      <button onClick={() => this.getChildNodes(node)}>
                        get children
                      </button>
                    </div>
                  ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default graphql(GetNodesAsObj, { name: "GetNodesAsObj" })(ManageNodes);
