import React, { Component } from "react";
import gql from "graphql-tag";
import { compose, graphql } from "react-apollo";

const GetFkNodes = gql`
  query {
    getFkNodes {
      nodeId
      nodeName
      pathNameString
      pathString
      pathLength
    }
  }
`;

const GetAmNodes = gql`
  query {
    getAmNodes {
      nodeId
      nodeName
      pathNameString
      pathString
      pathLength
    }
  }
`;

class ManageNodes extends Component {
  state = {};
  render() {
    console.log(this.props);

    const loadingFk = this.props.getFkNodes.loading;
    const getFkNodes = this.props.getFkNodes.getFkNodes;
    const loadingAm = this.props.getAmNodes.loading;
    const getAmNodes = this.props.getAmNodes.getAmNodes;

    if (loadingFk || loadingAm) {
      return false;
    }

    return (
      <div className="custom-outer-div">
        <div className="custom-box-container">
          <div className="inner-container">
            {/* <div className="header">Manage Huddlie Categories</div> */}
            <div className="header-no-border">
              <label className="label-with-underline">Manage Categories</label>
            </div>
            <div className="mc-row">
              <div id="retailer-outer-div" className="mc-row-50">
                <div className="header-no-border">Retailer Categories</div>
                <div className="custom-box-controller">
                  <div className="controller selected-controller">Flipkart</div>
                  <div className="controller">Amazon</div>
                </div>
                <div id="flipkart-outer-div">
                  Flipkart Nodes
                  {getFkNodes.map(node => (
                    <div key={node.nodeId}>
                      {/* <label>node id: {node.nodeId}</label> */}
                      <label>node name: {node.nodeName}</label>
                      {/* <label>path Name String{node.pathNameString}</label>
                      <label>path String{node.pathString}</label> */}
                      <label>path Length {node.pathLength}</label>
                    </div>
                  ))}
                </div>
                <div id="amazon-outer-div">
                  Amazon Nodes
                  {getAmNodes.map(node => (
                    <div key={node.nodeId}>
                      {/* <label>node id: {node.nodeId}</label> */}
                      <label>node name: {node.nodeName}</label>
                      {/* <label>path Name String{node.pathNameString}</label>
                      <label>path String{node.pathString}</label> */}
                      <label>path Length {node.pathLength}</label>
                    </div>
                  ))}
                </div>
              </div>
              <div id="huddlie-outer-div" className="mc-row-50">
                <div className="header-no-border">Huddlie Categories</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default compose(
  graphql(GetFkNodes, { name: "getFkNodes" }),
  graphql(GetAmNodes, { name: "getAmNodes" })
)(ManageNodes);
