import React from "react";
import {
  FaFile,
  FaFolder,
  FaFolderOpen,
  FaChevronDown,
  FaChevronRight
} from "react-icons/fa";
import styled from "styled-components";
import PropTypes from "prop-types";

const getPaddingLeft = level => {
  let paddingLeft = level * 20;
  return paddingLeft;
};

const StyledTreeNode = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  padding: 5px 8px;
  padding-left: ${props => getPaddingLeft(props.level)}px;
  &:hover {
    background: lightgray;
  }
`;

const NodeIcon = styled.div`
cursor:pointer
  padding: 5px 10px;
  font-size: 15px;
  margin-right: ${props => (props.marginRight ? props.marginRight : 5)}px;
`;

const TreeNode = props => {
  const { node, getChildNodes, level, onToggle, retailer } = props;

  return (
    <React.Fragment>
      <StyledTreeNode level={level}>
        <NodeIcon onClick={() => onToggle(retailer, node)}>
          {getChildNodes(retailer, node).length > 0 &&
            (node.isOpen ? <FaChevronDown /> : <FaChevronRight />)}
        </NodeIcon>

        {/* <span role="button" onClick={() => onNodeSelect(node)}> */}
        <span className="tree-node-name-span" role="button">
          {node.nodeName}
        </span>
        <button className="tree-edit-btn">Select</button>
      </StyledTreeNode>

      {node.isOpen &&
        getChildNodes(retailer, node).map(childNode => (
          <TreeNode
            {...props}
            node={childNode}
            level={level + 1}
            key={childNode.nodeId}
          />
        ))}
    </React.Fragment>
  );
};

TreeNode.propTypes = {
  node: PropTypes.object.isRequired,
  getChildNodes: PropTypes.func.isRequired,
  level: PropTypes.number.isRequired,
  onToggle: PropTypes.func.isRequired
};

TreeNode.defaultProps = {
  level: 0
};

export default TreeNode;
